package net.pladema.emergencycare.repository;import net.pladema.emergencycare.repository.domain.EmergencyCareVo;import net.pladema.emergencycare.repository.entity.EmergencyCareEpisode;import net.pladema.emergencycare.repository.entity.EmergencyCareState;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.stereotype.Repository;import org.springframework.transaction.annotation.Transactional;import java.util.List;@Repositorypublic interface EmergencyCareEpisodeRepository extends JpaRepository<EmergencyCareEpisode, Integer> {	@Transactional(readOnly = true)	@Query(value = " SELECT NEW net.pladema.emergencycare.repository.domain.EmergencyCareVo(ece.id, pe.firstName, pe.lastName, ece.patientId, ece.triageCategoryId," +			" ece.emergencyCareTypeId, ece.emergencyCareStateId, ece.doctorsOfficeId, dso.description, ece.creationable.createdOn) "+			" FROM EmergencyCareEpisode ece "+			" JOIN Patient pa ON (pa.id = ece.patientId) "+			" JOIN Person pe ON (pe.id = pa.personId) "+			" JOIN DoctorsOffice dso ON (dso.id = ece.doctorsOfficeId) "+			" WHERE ece.emergencyCareStateId =  "+EmergencyCareState.EN_ATENCION+" OR ece.emergencyCareStateId = "+EmergencyCareState.EN_ESPERA+			" AND ece.institutionId = :institutionId ")	List<EmergencyCareVo> getAll(@Param("institutionId") Integer institutionId);}