named-native-query.query:
WITH base AS ( SELECT ms.id AS statement_id, d.id AS document_id, d.source_id, d.source_type_id, d.type_id AS document_type, d.status_id AS document_status, ms.patient_id, s.pt, s.sctid, ms.status_id AS status_statement, ms.created_on, ms.updated_on, row_number() OVER (PARTITION BY ms.snomed_id, ms.patient_id ORDER BY ms.updated_on desc) AS rw, row_number() OVER (PARTITION BY ms.patient_id ORDER BY ms.updated_on desc) AS rww, ds.id AS d_id, ds.chronic, ds.start_date, ds.end_date, ds.suspended_start_date, ds.suspended_end_date, ms.health_condition_id FROM document d INNER JOIN document_medicamention_statement dms ON d.id = dms.document_id INNER JOIN medication_statement ms ON dms.medication_statement_id = ms.id INNER JOIN snomed s ON ms.snomed_id = s.id LEFT JOIN dosage ds ON ms.dosage_id = ds.id WHERE d.status_id IN ('445665009', '445667001') AND d.type_id IN (4, 5, 11, 9) -- AND (ms.updated_on>='2023-06-01' AND ms.updated_on <= '2023-07-01') AND ms.updated_on BETWEEN :startDate AND :endDate )
select concat(pe.first_name, ' ', pe.last_name) AS patientName, it.description AS patientIdType,pe.identification_number AS patientIdNumber, g.description AS patientSex, gg.description AS patientGender, pex.name_self_determination AS patientSelfPerceivedName, pe.birth_date AS patientBirthDate,
case when (EXTRACT(MONTH FROM b.created_on) - EXTRACT(MONTH FROM pe.birth_date)) < 0 THEN concat((EXTRACT(YEAR FROM b.created_on) - EXTRACT(YEAR FROM pe.birth_date)) -1, ' años, ', 12 +(EXTRACT(MONTH FROM b.created_on) - EXTRACT(MONTH FROM pe.birth_date)), ' meses') ELSE concat((EXTRACT(YEAR FROM b.created_on) - EXTRACT(YEAR FROM pe.birth_date)), ' años, ', (EXTRACT(MONTH FROM b.created_on) - EXTRACT(MONTH FROM pe.birth_date)), ' meses') END AS patientAgeWhenAttended, case when (EXTRACT(MONTH FROM CURRENT_TIMESTAMP) - EXTRACT(MONTH FROM pe.birth_date)) < 0 THEN concat((EXTRACT(YEAR FROM CURRENT_TIMESTAMP) - EXTRACT(YEAR FROM pe.birth_date)) -1, ' años, ', 12 +(EXTRACT(MONTH FROM CURRENT_TIMESTAMP) - EXTRACT(MONTH FROM pe.birth_date)), ' meses') ELSE concat((EXTRACT(YEAR FROM CURRENT_TIMESTAMP) - EXTRACT(YEAR FROM pe.birth_date)), ' años, ', (EXTRACT(MONTH FROM CURRENT_TIMESTAMP) - EXTRACT(MONTH FROM pe.birth_date)), ' meses') END AS patientAgeToday, b.pt AS medication, b.sctid AS snomed, msst.description AS status, dt.description AS origin, b.updated_on lastUpdate, case b.chronic when true THEN 'Si' ELSE 'No' END isChronic, b.start_date AS startDate, b.end_date AS endDate, b.suspended_start_date AS suspensionStartDate, b.suspended_end_date AS suspensionEndDate, concat(s_hc.pt, '(SNOMED: ', s_hc.sctid, ' | CIE10; ', hc.cie10_codes, ')') AS relatedProblem, hcty.description problemType, hcst.description problemStatus, hcsv.display severity, hcvr.description verification, case mr.id when null THEN 'No' ELSE 'Si' END AS wasPrescribed, mr.has_recipe AS hasPrescription, mrc.description AS prescriptionCategory, mrs.description AS prescriptionStatus, ri.description AS prescriptionType, concat(mc.name, '(RNOS: ', hi.rnos, ')') AS medicalInsurance, pmc.affiliate_number AS affiliateNumber, concat(ins.name, ' (SISA: ', ins.sisa_code, ')') AS institution, concat(p_hcp.first_name, ' ', p_hcp.last_name) AS professionalName, p_hcp.identification_number AS professionalIdNumber, hcp.license_number AS professionalLicense, (SELECT string_agg(psp.description, ', ') FROM professional_professions ppr INNER JOIN professional_specialty psp ON ppr.professional_specialty_id=psp.id WHERE ppr.healthcare_professional_id=hcp.id) AS professionalSpeciality, mr.request_date AS prescriptionDate, mr.updated_on AS prescriptionUpdateDate FROM base b INNER JOIN document_type dt ON b.document_type=dt.id INNER JOIN medication_statement_status msst ON b.status_statement = msst.id INNER JOIN patient pa ON b.patient_id = pa.id INNER JOIN person pe ON pa.person_id = pe.id INNER JOIN identification_type it ON pe.identification_type_id=it.id INNER JOIN gender g ON pe.gender_id=g.id LEFT JOIN person_extended pex ON pex.person_id=pe.id LEFT JOIN gender gg ON pex.gender_self_determination=gg.id LEFT JOIN health_condition hc ON b.health_condition_id = hc.id LEFT JOIN snomed s_hc ON hc.snomed_id=s_hc.id LEFT JOIN condition_clinical_status hcst ON hc.status_id=hcst.id LEFT JOIN health_condition_severity hcsv ON hc.severity=hcsv.code LEFT JOIN condition_verification_status hcvr ON hc.verification_status_id=hcvr.id LEFT JOIN problem_type hcty ON hc.problem_id=hcty.id LEFT JOIN medication_request mr ON mr.id=b.source_id AND b.source_type_id=2 LEFT JOIN medication_request_status mrs ON mr.status_id=mrs.id LEFT JOIN medication_request_category mrc ON mr.category_id=mrc.id LEFT JOIN request_intent ri ON mr.intent_id=ri.id LEFT JOIN institution ins ON mr.institution_id=ins.id LEFT JOIN healthcare_professional hcp ON mr.doctor_id=hcp.id LEFT JOIN person p_hcp ON hcp.person_id=p_hcp.id LEFT JOIN patient_medical_coverage pmc ON mr.medical_coverage_id=pmc.id LEFT JOIN medical_coverage mc ON pmc.medical_coverage_id=mc.id LEFT JOIN health_insurance hi ON mc.id=hi.id where b.rw=1 AND b.status_statement = '55561003' AND ins.id=:institutionId AND (SELECT MAX(braw.rww) FROM base braw WHERE braw.patient_id=b.patient_id) >= 5 AND (EXTRACT(YEAR FROM b.created_on) - EXTRACT(YEAR FROM pe.birth_date)) >=60 ORDER BY b.patient_id, b.pt;
