SELECT concat(ins.name, '(SISA: ', ins.sisa_code, ' | CUIT: ', ins.cuit, ')') AS institution, cs.name as operativeUnit, concat(p.first_name, ' ', p.middle_names, ' ', p.last_name, ' ', p.other_last_names) as lender, p.identification_number as lenderDni, case when (EXTRACT(HOUR FROM oc.created_on)) < 3 then (oc.start_date -1) else oc.start_date end as attentionDate,
to_char(oc.created_on AT TIME ZONE 'UTC+0', 'HH24:MI') as hour, (row_number() over(partition by oc.doctor_id order by oc.start_date)) as consultationNumber, pp.identification_number as patientDni, concat(pp.first_name , ' ', pp.last_name) as patientName, g.description as gender, pp.birth_date as birthDate, case when (EXTRACT(MONTH FROM oc.start_date) - EXTRACT(MONTH FROM pp.birth_date)) < 0 then concat((EXTRACT(YEAR FROM oc.start_date) - EXTRACT(YEAR FROM pp.birth_date)) -1, ' Años, ', 12 +(EXTRACT(MONTH FROM oc.start_date) - EXTRACT(MONTH FROM pp.birth_date)), ' Meses') else concat((EXTRACT(YEAR FROM oc.start_date) - EXTRACT(YEAR FROM pp.birth_date)), ' Años, ', (EXTRACT(MONTH FROM oc.start_date) - EXTRACT(MONTH FROM pp.birth_date)), ' Meses') end as ageTurn, case when (EXTRACT(MONTH FROM CURRENT_TIMESTAMP) - EXTRACT(MONTH FROM pp.birth_date)) < 0 then concat((EXTRACT(YEAR FROM CURRENT_TIMESTAMP) - EXTRACT(YEAR FROM pp.birth_date)) -1, ' Años, ', 12 +(EXTRACT(MONTH FROM CURRENT_TIMESTAMP) - EXTRACT(MONTH FROM pp.birth_date)), ' Meses') else concat((EXTRACT(YEAR FROM CURRENT_TIMESTAMP) - EXTRACT(YEAR FROM pp.birth_date)), ' Años, ', (EXTRACT(MONTH FROM CURRENT_TIMESTAMP) - EXTRACT(MONTH FROM pp.birth_date)), ' Meses') end as ageToday, concat(mc.name, '(RNOS: ', hi.rnos, ')') as medicalCoverage, concat(a.street, ' N° ', a.number, case when a.floor is not null then concat(' Piso: ', a.floor, 'Departamento: ', a.apartment) end) as address, c.description as location,
(SELECT string_agg(r.description, ', ') from outpatient_consultation_reasons ocr INNER JOIN reasons r on ocr.reason_id=r.id where ocr.outpatient_consultation_id=oc.id) as reasons, (SELECT string_agg(concat(s_proc.pt, '(', ps.description, ' | SNOMED: ', s_proc.sctid, ' | CIE10: ', proc.cie10_codes, ')'), ', ') from document_procedure dp INNER JOIN procedures proc on dp.procedure_id=proc.id INNER JOIN procedures_status ps on proc.status_id=ps.id INNER JOIN snomed s_proc on proc.snomed_id=s_proc.id where dp.document_id=d.id) as procedures, (SELECT string_agg(concat(s_prob.pt,' (',ccs.description,' & ',cvs.description,' - Fecha: ',hc.start_date, ' - Tipo: ', pt.description,')', '[SNOMED: ', s_prob.sctid, ' | CIE10: ', hc.cie10_codes, ']'), ', ') from document_health_condition dhc INNER JOIN health_condition hc on dhc.health_condition_id=hc.id INNER JOIN problem_type pt on hc.problem_id=pt.id INNER JOIN snomed s_prob on hc.snomed_id=s_prob.id INNER JOIN condition_clinical_status ccs on hc.status_id=ccs.id INNER JOIN condition_verification_status cvs on hc.verification_status_id=cvs.id where dhc.document_id=d.id and hc.problem_id<>'57177007') as problems, (SELECT string_agg(concat(s_med.pt, '[SNOMED: ', s_med.sctid, ' | CIE10: ', ms.cie10_codes, '] (status: ', mss.description, ')'), ', ') FROM document_medicamention_statement dms INNER JOIN medication_statement ms on dms.medication_statement_id=ms.id INNER JOIN medication_statement_status mss on ms.status_id=mss.id INNER JOIN snomed s_med on ms.snomed_id=s_med.id WHERE dms.document_id=d.id) as medication, (SELECT string_agg(ev.description, ', ') from note ev where d.other_note_id=ev.id) as evolution FROM outpatient_consultation oc LEFT JOIN institution ins on oc.institution_id=ins.id LEFT JOIN clinical_specialty cs on oc.clinical_specialty_id=cs.id LEFT JOIN healthcare_professional hp ON oc.doctor_id=hp.id INNER JOIN person p on hp.person_id=p.id LEFT JOIN patient pa on oc.patient_id=pa.id INNER JOIN person pp on pa.person_id=pp.id LEFT JOIN person_extended pe on pe.person_id=pp.id INNER JOIN gender g on pp.gender_id=g.id LEFT JOIN gender gg on pe.gender_self_determination=gg.id LEFT JOIN patient_medical_coverage pmc ON pmc.patient_id=oc.patient_medical_coverage_id LEFT JOIN medical_coverage mc on pmc.medical_coverage_id=mc.id LEFT JOIN health_insurance hi on hi.id=mc.id LEFT JOIN address a on pe.address_id=a.id LEFT JOIN city c on a.city_id=c.id LEFT JOIN document d on oc.document_id=d.id WHERE oc.created_on BETWEEN :startDate AND :endDate AND (pmc.id is not null AND NOT (lower(mc.name) like '%sumar%')) AND ins.id = :institutionId order by oc.created_on;

SELECT concat(ins.name, '(SISA: ', ins.sisa_code, ' | CUIT: ', ins.cuit, ')') AS institution,
concat(p.first_name, ' ', p.middle_names, ' ', p.last_name, ' ', p.other_last_names) as lender, p.identification_number as lenderDni, case when (EXTRACT(HOUR FROM oc.created_on)) < 3 then (oc.start_date -1) else oc.start_date end as attentionDate,
to_char(oc.created_on AT TIME ZONE 'UTC+0', 'HH24:MI') as attentionHour, (row_number() over(partition by oc.doctor_id order by oc.start_date)) as consultationNumber, pp.identification_number as patientDni, concat(pp.first_name , ' ', pp.last_name) as patientName, g.description as gender, pp.birth_date birthDate, case when (EXTRACT(MONTH FROM oc.start_date) - EXTRACT(MONTH FROM pp.birth_date)) < 0 then concat((EXTRACT(YEAR FROM oc.start_date) - EXTRACT(YEAR FROM pp.birth_date)) -1, ' Años, ', 12 +(EXTRACT(MONTH FROM oc.start_date) - EXTRACT(MONTH FROM pp.birth_date)), ' Meses') else concat((EXTRACT(YEAR FROM oc.start_date) - EXTRACT(YEAR FROM pp.birth_date)), ' Años, ', (EXTRACT(MONTH FROM oc.start_date) - EXTRACT(MONTH FROM pp.birth_date)), ' Meses') end as ageTurn, case when (EXTRACT(MONTH FROM CURRENT_TIMESTAMP) - EXTRACT(MONTH FROM pp.birth_date)) < 0 then concat((EXTRACT(YEAR FROM CURRENT_TIMESTAMP) - EXTRACT(YEAR FROM pp.birth_date)) -1, ' Años, ', 12 +(EXTRACT(MONTH FROM CURRENT_TIMESTAMP) - EXTRACT(MONTH FROM pp.birth_date)), ' Meses') else concat((EXTRACT(YEAR FROM CURRENT_TIMESTAMP) - EXTRACT(YEAR FROM pp.birth_date)), ' Años, ', (EXTRACT(MONTH FROM CURRENT_TIMESTAMP) - EXTRACT(MONTH FROM pp.birth_date)), ' Meses') end as ageToday, (SELECT string_agg(concat(mc.name, '(RNOS: ', hi.rnos, ')'), ', ') from patient_medical_coverage pmc LEFT JOIN medical_coverage mc on pmc.medical_coverage_id=mc.id LEFT JOIN health_insurance hi on hi.id=mc.id where pmc.patient_id=pa.id) as medicalCoverage, concat(a.street, ' N° ', a.number, case when a.floor is not null then concat(' Piso: ', a.floor, 'Departamento: ', a.apartment) end) as direction, c.description as patientLocation, pe.phone_number as phoneNumber, (SELECT string_agg(concat(s_prob.pt,' (',ccs.description,' & ',cvs.description,' - Fecha: ',hc.start_date ,')', '[SNOMED: ', s_prob.sctid, ' | CIE10: ', hc.cie10_codes, ']'), ', ') from document_health_condition dhc INNER JOIN health_condition hc on dhc.health_condition_id=hc.id INNER JOIN problem_type pt on hc.problem_id=pt.id INNER JOIN snomed s_prob on hc.snomed_id=s_prob.id INNER JOIN condition_clinical_status ccs on hc.status_id=ccs.id INNER JOIN condition_verification_status cvs on hc.verification_status_id=cvs.id where dhc.document_id=d.id) as problems from outpatient_consultation oc LEFT JOIN institution ins on oc.institution_id=ins.id LEFT JOIN clinical_specialty cs on oc.clinical_specialty_id=cs.id LEFT JOIN healthcare_professional hp ON oc.doctor_id=hp.id INNER JOIN person p on hp.person_id=p.id LEFT JOIN patient pa on oc.patient_id=pa.id INNER JOIN person pp on pa.person_id=pp.id LEFT JOIN person_extended pe on pe.person_id=pp.id INNER JOIN gender g on pp.gender_id=g.id LEFT JOIN gender gg on pe.gender_self_determination=gg.id LEFT JOIN patient_medical_coverage pmc ON pmc.patient_id=pa.id LEFT JOIN medical_coverage mc on pmc.medical_coverage_id=mc.id LEFT JOIN address a on pe.address_id=a.id LEFT JOIN city c on a.city_id=c.id LEFT JOIN document d on oc.document_id=d.id WHERE oc.created_on BETWEEN :startDate AND :endDate AND EXTRACT(year FROM age(oc.start_date, pp.birth_date)) >= 60 AND ins.id = :institutionId order by oc.created_on;

WITH base AS ( SELECT ms.id AS statement_id, d.id AS document_id, d.source_id, d.source_type_id, d.type_id AS document_type, d.status_id AS document_status, ms.patient_id, s.pt, s.sctid, ms.status_id AS status_statement, ms.created_on, ms.updated_on, row_number() OVER (PARTITION BY ms.snomed_id, ms.patient_id ORDER BY ms.updated_on desc) AS rw, row_number() OVER (PARTITION BY ms.patient_id ORDER BY ms.updated_on desc) AS rww, ds.id AS d_id, ds.chronic, ds.start_date, ds.end_date, ds.suspended_start_date, ds.suspended_end_date, ms.health_condition_id FROM document d INNER JOIN document_medicamention_statement dms ON d.id = dms.document_id INNER JOIN medication_statement ms ON dms.medication_statement_id = ms.id INNER JOIN snomed s ON ms.snomed_id = s.id LEFT JOIN dosage ds ON ms.dosage_id = ds.id WHERE d.status_id IN ('445665009', '445667001') AND d.type_id IN (4, 5, 11, 9) -- AND (ms.updated_on>='2023-06-01' AND ms.updated_on <= '2023-07-01') AND ms.updated_on BETWEEN :startDate AND :endDate )
select concat(pe.first_name, ' ', pe.last_name) AS patientName, it.description AS patientIdType,pe.identification_number AS patientIdNumber, g.description AS patientSex, gg.description AS patientGender, pex.name_self_determination AS patientSelfPerceivedName, pe.birth_date AS patientBirthDate,
case when (EXTRACT(MONTH FROM b.created_on) - EXTRACT(MONTH FROM pe.birth_date)) < 0 THEN concat((EXTRACT(YEAR FROM b.created_on) - EXTRACT(YEAR FROM pe.birth_date)) -1, ' años, ', 12 +(EXTRACT(MONTH FROM b.created_on) - EXTRACT(MONTH FROM pe.birth_date)), ' meses') ELSE concat((EXTRACT(YEAR FROM b.created_on) - EXTRACT(YEAR FROM pe.birth_date)), ' años, ', (EXTRACT(MONTH FROM b.created_on) - EXTRACT(MONTH FROM pe.birth_date)), ' meses') END AS patientAgeWhenAttended, case when (EXTRACT(MONTH FROM CURRENT_TIMESTAMP) - EXTRACT(MONTH FROM pe.birth_date)) < 0 THEN concat((EXTRACT(YEAR FROM CURRENT_TIMESTAMP) - EXTRACT(YEAR FROM pe.birth_date)) -1, ' años, ', 12 +(EXTRACT(MONTH FROM CURRENT_TIMESTAMP) - EXTRACT(MONTH FROM pe.birth_date)), ' meses') ELSE concat((EXTRACT(YEAR FROM CURRENT_TIMESTAMP) - EXTRACT(YEAR FROM pe.birth_date)), ' años, ', (EXTRACT(MONTH FROM CURRENT_TIMESTAMP) - EXTRACT(MONTH FROM pe.birth_date)), ' meses') END AS patientAgeToday, b.pt AS medication, b.sctid AS snomed, msst.description AS status, dt.description AS origin, b.updated_on lastUpdate, case b.chronic when true THEN 'Si' ELSE 'No' END isChronic, b.start_date AS startDate, b.end_date AS endDate, b.suspended_start_date AS suspensionStartDate, b.suspended_end_date AS suspensionEndDate, concat(s_hc.pt, '(SNOMED: ', s_hc.sctid, ' | CIE10; ', hc.cie10_codes, ')') AS relatedProblem, hcty.description problemType, hcst.description problemStatus, hcsv.display severity, hcvr.description verification, case mr.id when null THEN 'No' ELSE 'Si' END AS wasPrescribed, mr.has_recipe AS hasPrescription, mrc.description AS prescriptionCategory, mrs.description AS prescriptionStatus, ri.description AS prescriptionType, concat(mc.name, '(RNOS: ', hi.rnos, ')') AS medicalInsurance, pmc.affiliate_number AS affiliateNumber, concat(ins.name, ' (SISA: ', ins.sisa_code, ')') AS institution, concat(p_hcp.first_name, ' ', p_hcp.last_name) AS professionalName, p_hcp.identification_number AS professionalIdNumber, hcp.license_number AS professionalLicense, (SELECT string_agg(psp.description, ', ') FROM professional_professions ppr INNER JOIN professional_specialty psp ON ppr.professional_specialty_id=psp.id WHERE ppr.healthcare_professional_id=hcp.id) AS professionalSpeciality, mr.request_date AS prescriptionDate, mr.updated_on AS prescriptionUpdateDate FROM base b INNER JOIN document_type dt ON b.document_type=dt.id INNER JOIN medication_statement_status msst ON b.status_statement = msst.id INNER JOIN patient pa ON b.patient_id = pa.id INNER JOIN person pe ON pa.person_id = pe.id INNER JOIN identification_type it ON pe.identification_type_id=it.id INNER JOIN gender g ON pe.gender_id=g.id LEFT JOIN person_extended pex ON pex.person_id=pe.id LEFT JOIN gender gg ON pex.gender_self_determination=gg.id LEFT JOIN health_condition hc ON b.health_condition_id = hc.id LEFT JOIN snomed s_hc ON hc.snomed_id=s_hc.id LEFT JOIN condition_clinical_status hcst ON hc.status_id=hcst.id LEFT JOIN health_condition_severity hcsv ON hc.severity=hcsv.code LEFT JOIN condition_verification_status hcvr ON hc.verification_status_id=hcvr.id LEFT JOIN problem_type hcty ON hc.problem_id=hcty.id LEFT JOIN medication_request mr ON mr.id=b.source_id AND b.source_type_id=2 LEFT JOIN medication_request_status mrs ON mr.status_id=mrs.id LEFT JOIN medication_request_category mrc ON mr.category_id=mrc.id LEFT JOIN request_intent ri ON mr.intent_id=ri.id LEFT JOIN institution ins ON mr.institution_id=ins.id LEFT JOIN healthcare_professional hcp ON mr.doctor_id=hcp.id LEFT JOIN person p_hcp ON hcp.person_id=p_hcp.id LEFT JOIN patient_medical_coverage pmc ON mr.medical_coverage_id=pmc.id LEFT JOIN medical_coverage mc ON pmc.medical_coverage_id=mc.id LEFT JOIN health_insurance hi ON mc.id=hi.id where b.rw=1 AND b.status_statement = '55561003' AND ins.id=:institutionId AND (SELECT MAX(braw.rww) FROM base braw WHERE braw.patient_id=b.patient_id) >= 5 AND (EXTRACT(YEAR FROM b.created_on) - EXTRACT(YEAR FROM pe.birth_date)) >=60 ORDER BY b.patient_id, b.pt;
